import React from 'react';
import Navbar from './components/Navbar';
import './App.css';
import Home from './components/pages/Home';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import DrawGames from './components/pages/DrawGames';
import Products from './components/pages/Products';
import SignUp from './components/pages/SignUp';
import ScrollToTop from './components/ScrollToTop';

function App() {
  window.onbeforeunload = function () {
    window.scrollTo(0, 0);
  };

  return (
    <>
      <Router className="router-test">
        <Navbar className="router-test" />
        <Switch className="router-test">
          <ScrollToTop className="router-test">
            <Route className="router-test" path="/" exact component={Home} />
          </ScrollToTop>
        </Switch>
        <Switch className="router-test">
          <Route
            className="router-test"
            path="/draw-games"
            component={DrawGames}
          />
          <Route
            className="router-test"
            path="/products"
            component={Products}
          />
          <Route className="router-test" path="/sign-up" component={SignUp} />
        </Switch>
      </Router>
    </>
  );
}

export default App;
